mapper.select:
select suec.id, suec.sign_up_time AS signUpTime, suec.is_payment AS isPayment, s.name AS studentName, s.id AS studentId, s.guardian_phone studentPhone, ec.title AS experienceCourseName, ec.id AS experienceCourseId, sc.share_counts shareCounts from sign_up_experience_course AS suec LEFT JOIN experience_course AS ec ON ec.id=suec.experience_course_id LEFT JOIN student AS s ON s.id=suec.student_id LEFT JOIN share_circle AS sc ON sc.course_id=suec.id and sc.student_id=s.id where ec.status=1 and suec.status=1 and s.status=1 and s.id is not null and ec.id is not null
and s.name=#{studentName}
and suec.is_payment=#{isPayment}
