mapper.select:
SELECT ssu.id,ssu.is_payment AS isPayment,ssu.is_halfway AS isHalfway,ssu.sign_up_time as signUpTime, s.id AS studentId,s.name AS studentName,s.guardian_phone AS studentPhone, g.id AS gradeId,g.name AS gradeName, c.id AS courseId,c.name AS courseName,c.money AS courseMoney, c.status AS courseStatus FROM student_sign_up AS ssu LEFT JOIN student AS s on ssu.student_id=s.id LEFT JOIN grade AS g on ssu.grade_id=g.id LEFT JOIN course AS c on g.course_id=c.id where ssu.status=1 and s.status=1 and g.status=1 and s.id is not NULL and g.id is not NULL and c.id is not NULL
and s.name=#{studentName}
and ssu.is_payment=#{isPayment}
